sourcesContent:
import mongoose, { Mongoose } from "mongoose";

const Schema = mongoose.Schema;

const categorySchema= new Schema({
    name:{
        type:String,
        required:true,
    },
    description:{
        type:String,
        default:null,
    },
    image:{
        type:String,
